import React, { Component } from 'react';
import { render } from "react-dom";
import { Chrono } from "react-chrono";
import { ThemeConsumer } from 'styled-components';

const items = [{
    title: "Apr 2021",
    cardTitle: "AfterClass Intern",
    cardSubtitle:
      "Men of the British Expeditionary Force (BEF) wade out to a destroyer during the evacuation from Dunkirk."
},
{
    title: "Jan 2021",
    cardTitle: "BIA Data Associate Programme",
    cardDetailedText: "Working on a coding language classifier using Machine Learning"
},
{
    title: "May 1940",
    cardTitle: "Dunkirk",
    cardSubtitle:
      "Men of the British Expeditionary Force (BEF) wade out to a destroyer during the evacuation from Dunkirk.",
    cardDetailedText: `On 10 May 1940, Hitler began his long-awaited offensive in the west by invading neutral Holland and Belgium and attacking northern France. Holland capitulated after only five days of fighting, and the Belgians surrendered on 28 May. With the success of the German ‘Blitzkrieg’, the British Expeditionary Force and French troops were in danger of being cut off and destroyed.`
}];

class NewTimeline extends Component {
    render() {
        return (
            <div style={{ width: "100%", height: "100%" }}>
            <Chrono
              items={items}
              mode="VERTICAL_ALTERNATING"
              hideControls="true"
              theme={{primary: "#cdcdcd", secondary: "#02024d", cardBgColor: "#01013d", cardForeColor: "#cdcdcd" }} 
            />
            <Chrono mode="VERTICAL">
            <div>
            <p>Lorem Ipsum. Lorem Ipsum. Lorem Ipsum</p>
            </div>
            <div>
            <img src="<url to  a nice image" />
            </div>
            </Chrono>
          </div>
        );
    }
}

export default NewTimeline;
